import {Group,Input,FormInputLabel} from './form-input.styles.jsx';

const FormInput = ({label,...otherProps}) => {
    return (
    <Group> 
        <Input {...otherProps}/>               
        { label &&
         <label className={`${
            otherProps.value.length ? 'shrink' : ''
            } form-input-label`}
             >
            {label}
         </label>
        }         
    </Group>
 );
};

export default FormInput;
